revision.text:
/**
 * Keep code in MediaWiki:Common.js to a minimum as it is unconditionally
 * loaded for all users on every wiki page. If possible create a gadget that is
 * enabled by default instead of adding it here (since gadgets are fully
 * optimized ResourceLoader modules with possibility to add dependencies etc.)
 *
 * Since Common.js isn't a gadget, there is no place to declare its
 * dependencies, so we have to lazy load them with mw.loader.using on demand and
 * then execute the rest in the callback. In most cases these dependencies will
 * be loaded (or loading) already and the callback will not be delayed. In case a
 * dependency hasn't arrived yet it'll make sure those are loaded before this.
 */

/* global mw, $, importStylesheet, importScript */
/* jshint curly:false eqnull:true, strict:false, browser:true */

mw.loader.using( ['mediawiki.util'], function () {
/* Begin of mw.loader.using callback */

/**
 * Fix for Windows XP Unicode font rendering
 */
if ( navigator.appVersion.search(/windows nt 5/i) !== -1 ) {
    mw.util.addCSS( '.IPA { font-family: "Lucida Sans Unicode", "Arial Unicode MS"; } ' + 
                '.Unicode { font-family: "Arial Unicode MS", "Lucida Sans Unicode"; } ' );
}

/* End of mw.loader.using callback */
} );
/* DO NOT ADD CODE BELOW THIS LINE */


/************************************
 *    ATT&CK Technique Matrix
 ************************************/
$(function() {

    // Transpose tables with "pre_transpose"
    var table_pre = $('table.pre_transpose');
    if (table_pre ) {
        var tbody = table_pre.find('tbody');
        var row = tbody.find('tr')
        var long_row= 0;
        row.each(function(index, value) {
            var currRow= $(value);
            var len= currRow.find('td,th').length;
            long_row= Math.max(len, long_row);
        });

        var new_body = $('<tbody></tbody>');
        for (i = 0; i < long_row; i++){
            var new_row= $('<tr></tr>');
            row.each(function(index, value) {
                var cell = $(value).find('td,th');
                cell = cell.length === 0 ? $('<td></td>') : cell.eq(0);
                new_row.append(cell);
            });
            new_body.append(new_row);
        }
        tbody.remove();
        table_pre.append(new_body);
        table_pre.show();
        table_pre.removeClass('pre_transpose');
    }

    // Colorize the cells based off of the URL
    // TODO: change these to use ID. Will allow for more robust rules, and a shorter URL
    if ($('table.technique_matrix')) {
        function colorize(coverage, cells) {
            for (var i in cells) {
                if (cells[i]) {
                    $("table.technique_matrix td#" + cells[i]).removeClass("default_color").addClass(coverage);
                }
            }
        }

        if ($("table.colorized").length === 0) {
            return 
        } 

        var color_map = {
            good: 'high',
            moderate: 'medium',
            low: 'low',
            bad: 'none',
        };

        $("table.colorized td.technique").addClass("default_color");

        var count_map = {total: 0};
        for (var key in color_map) {
            var cells = mw.util.getParamValue(key);
            if (cells) {
                colorize(color_map[key], cells.split(','));
                count_map[key] = cells.split(',').length;
            } else {
                count_map[key] = 0;
            }
            count_map.total += count_map[key];
        }

        var default_color = color_map[mw.util.getParamValue('default')];
        if (default_color) {
             $("table.colorized .technique.default_color").removeClass("default_color").addClass(default_color);
        }

        // Now add a summary table
        if (count_map.total && mw.util.getParamValue('summary')) {
            var new_content = '<p></p><table class="wikitable"><tbody><thead><tr><th colspan="3">Technique Coverage</th></tr></thead>';
            for (var key in count_map) {
                var perc = count_map[key] / count_map.total;
                var perc = Math.floor(perc*1000) / 10;
                new_content += '<tr><th>' + key + '</th><td>' + count_map[key] + '</td><td>' + perc + '%</td></tr>';
            }
            new_content += '</tbody></table>'
            $('#mw-content-text').append(new_content);
        }
    }

    // Generate the heat map if specified by URL parameter
    if ($('table.heat_map') && mw.util.getParamValue('heat_map')) {
        $('table.technique_matrix td').addClass('cold');
        $('table.heat_map td').each(function(index, elem) {
           var technique = $(elem).text();
           $("table.technique_matrix td:contains('".concat(technique , "')")).filter(function() {
               return $(this).text() === technique;
           }).removeClass('cold').addClass('hot');
        });
    }

    // BETA: Testing for tagging the techniques with malware and groups
    if ($('table.mwg td')) {
        var cells = {};
        $('table.mwg td').each(function(i, cell) {
            cells[cell.id] = cell.innerText;
        });
        console.log(cells);
        var parent = $('table.mwg').parent();
        $('table.mwg').remove();
        parent.prepend('<div class="mwg_inputs"/>');
        var $mwg_inputs = $('div.mwg_inputs');
        console.log($mwg_inputs);
        for (var group in cells) {
            var val = cells[group];
            console.log(group, val);
            var html = '<span>' + group + '<input type="checkbox" id="' + val + '"><br/>' + '</span>';
            console.log(html);
            $mwg_inputs.append(html);
        }
        $('div.mwg_inputs span').sort();
    }
});
/*********************************************
 *     END OF TECHNIQUE MATRIX
 *********************************************/

function addPanelToSidebar( link, text, cls, dataShowCount ) {
	var node = document.getElementById( 'mw-panel' );
	var divNode = document.createElement( 'div' );
	var aNode = document.createElement( 'a' );
	var liNode = document.createElement( 'li' );
	aNode.appendChild( document.createTextNode( text ) );
	aNode.setAttribute( 'href', link );
	aNode.setAttribute( 'class', cls);
	aNode.setAttribute( 'data-show-count', dataShowCount);
	divNode.appendChild( aNode );
	divNode.setAttribute('class', 'portal' );
	node.appendChild( divNode );
}

function addTwitter() {
	addPanelToSidebar( 'https://twitter.com/MITREattack', 'Follow @MITREattack', 'twitter-follow-button', 'false' );
}

jQuery( addTwitter );

!function(d,s,id){var js,fjs=d.getElementsByTagName(s)[0],p=/^http:/.test(d.location)?'http':'https';if(!d.getElementById(id)){js=d.createElement(s);js.id=id;js.src=p+'://platform.twitter.com/widgets.js';fjs.parentNode.insertBefore(js,fjs);}}(document, 'script', 'twitter-wjs');

function ModifySidebar( name ) {
	try {
		var list = document.getElementById( 'p-tb' )
						   .getElementsByTagName( 'div' )[0]
						   .getElementsByTagName( 'ul' )[0];

		var listelements = list.getElementsByTagName( 'li' );

		for ( var i = 0; i < listelements.length; i++ ) {
			if (
				listelements[i].getElementsByTagName( 'a' )[0].innerHTML == name 
			)
			{
				list.removeChild( listelements[i] );
			}
		}
	} catch( e ) {
		// let's just ignore what's happened
		return;
	}
}

function CustomizeModificationsOfSidebar() {
	ModifySidebar( 'What links here' );
	ModifySidebar( 'Related changes' );
	ModifySidebar( 'Special pages' );
	ModifySidebar( 'Page information' );
	ModifySidebar( 'Browse properties');
}

function isObject( obj ) {
	return typeof obj == 'object' && obj != null;
}

function isArray( obj ) {
	return isObject( obj ) && obj.constructor.toString().indexOf( 'Array' ) != -1;
}

Array.prototype.Contains = function( element, strict ) {
	for( i in this ) {
		if( this[i] == element && !strict || this[i] === element ) {
			return true;
		}
	}
	return false;
}

if ( isArray( wgUserGroups ) ) {
	if ( !wgUserGroups.Contains( 'user' ) ) {
		jQuery( CustomizeModificationsOfSidebar );
	}
}

